section.code-block:
char st1[] = "\t value    \n";

// CHOICE:
__vic::trim(st1);       // result: "value"
__vic::trim_left(st1);  // result: "value    \n"
__vic::trim_right(st1); // result: "\t value"

std::string st2("...value123");

// CHOICE:
// trim dot chars
__vic::trim_left(st1, '.');          // result: "value123"
// trim all digits
__vic::trim_right(st1, "123456789"); // result: "...value"

